import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-extrato',
  templateUrl: './extrato.component.html',
  styleUrls: ['./extrato.component.scss']
})
export class ExtratoComponent implements OnInit {
  @Input() transferencias: any[];

  constructor() { }

  ngOnInit(): void {
  }

}
<table class="tabela">
  <thead class="tabela__cabecalho">
    <th class="tabela__cabecalho">Data</th>
    <th class="tabela__cabecalho">Valor</th>
    <th class="tabela__cabecalho">Destino</th>
  </thead>
  <tbody>
    <tr *ngFor="let transferencia of transferencias">
      <td>{{ transferencia.data | date: "short" }}</td>
      <td>{{ transferencia.valor | currency}}</td>
      <td>{{ transferencia.destino }}</td>
    </tr>
  </tbody>
</table>
