import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-nova-transferencia',
  templateUrl: './nova-transferencia.component.html',
  styleUrls: ['./nova-transferencia.component.scss'],
})
export class NovaTransferenciaComponent {

  @Output() aoTransferir = new EventEmitter<any>();

  valor: number;
  destino: number;

  //Usando o this, para especificar que as variáveis são dessa classe
  transferir() {
    console.log('Solicitada Nova Transferência');
    // console.log('Valor: ', this.valor);
    // console.log('Destino: ', this.destino);

    //o valor vai receber o valor da minha propriedade, do meu atributo valor e o destino vai receber o valor do atributo destino. Uso o this.valor e o this.destino. Crio uma variável par armazenar esse valor;
    const valorEmitir = {valor: this.valor, destino: this.destino};
    //utilizando o emit() para emitir um valor. Emitindo esswe evento, eu propago um valor.
    this.aoTransferir.emit(valorEmitir);
    this.limparCampos();
  }

  limparCampos(){
    this.valor = 0;
    this.destino = 0;

}
}
<section class="container">
  <form class="formulario" (ngSubmit)="transferir()">
    <h2 class="formulario__titulo">Nova Transferência</h2>

    <div class="form-field">
      <label class="form-field__label" for="valor">Valor</label>
      <input class="form-field__input" id="valor" type="" [(ngModel)]="valor" name="valor"/>
    </div>

    <div class="form-field">
      <label class="form-field__label" for="destino">Destino</label>
      <input class="form-field__input" id="destino" type="text" [(ngModel)]="destino" name="destino">
    </div>

    <div class="botao-wrapper">
      <button class="botao" type="submit">Transferir</button>
    </div>
  </form>
</section>
